    $(function () {
        var flag = true;
        $('iframe').on('load', function () {
            if (flag) {
                var poster = oEditors.getById["P-Poster"];
                var oldFunc = poster.htRunOptions.fnOnAppReady;
                poster.htRunOptions.fnOnAppReady = function () {                   
                    oldFunc();
                    poster.elEditingAreaContainer.getElementsByTagName('iframe')[0].contentWindow.document.querySelector('body.se2_inputarea').addEventListener("input", function () {
                        textLimit(this);
                    }, false);
                    $(poster.elEditingAreaContainer).find('.se2_input_htmlsrc,.se2_input_text').on("propertychange change keyup paste input", function () {
                        textLimit(this);
                    });
                }
                flag = false;
            }
        })

        oldVal = "";
        function textLimit(ele) {
            var nodeName = ele.nodeName;
            var text = nodeName === "BODY" ? ele.innerHTML : ele.value;

            var stringByteLength = (function (s, b, i, c) {
                for (b = i = 0; c = s.charCodeAt(i++); b += c >> 11 ? 3 : c >> 7 ? 2 : 1);
                return b
            })(text);

            if (stringByteLength > 500000) {
                alert("입력된 정보가 너무 많습니다.\n이벤터스는 쾌적한 사용자 환경을 위해 500,000byte의 이내의 데이터만 유효합니다.\n복사한 항목이 너무 크진 않은지 확인해주세요.\n(HTML태그, 문서 양식 등..)");
                if (nodeName === "BODY") {
                    ele.innerHTML = oldVal;
                } else {
                    ele.value = oldVal;
                }
            } else {
                oldVal = text;
            }
        }
    })
